<?phprequire_once 'config.php';$con = new MySQLi(DB_HOST, DB_USER, DB_PASS, DB_NAME);$SQL = mysqli_query($con,"SELECT *FROM sample_dataWHERE 1 = 1AND vps = 1ORDER BY id ASC");$rows = array();  while ($record = mysqli_fetch_assoc($SQL)) {    if ($record['total_pips']) {        $category[]  = $record['notes'].'<br>'.date('d-m-Y-H-i' , strtotime( $record['date_created'] ) ) ;         $rows1[] =  (int)$record['total_pips'] ;        $rows2[] =   (int)$record['cumulative_pips'] ;        // play, varied, rev, max, pause.        if( $record['play'] != null ){            $data_play[]  = array( 'x' =>  (int)$record['total_pips'] ) ;        }         if(  $record['pause']  != null ){            $data_pause[] =  array( 'x' => (int)$record['total_pips'] ) ;        }      }}//var_dump( json_encode(  array('type' => 'flags' , 'data' => array( $data_play ) , 'onSeries' => 'as-1')  ) );exit();$dataArr['series']['category']  = $category ;$dataArr['series']['data']  = array(     array( 'id' => 'as-1' ,'name' => 'TOTAL PIPS', 'data' => $rows1  ) ,     array('id' => 'as-2' ,'name' => 'CUMULATIVE PIPS', 'data' => $rows2 ) );    $dataArr['series']['data'][] = array('type' => 'flags' , 'data' => array_values( array_map("unserialize", array_unique(array_map("serialize",$data_play   ))) )   ,   'title' => ' ' ,'onSeries' => 'as-1' , 'shape' => 'triangle-down', 'width' => 6, 'height' => 6,'color' => 'yellow' , 'fillColor' => 'yellow') ; $dataArr['series']['data'][] = array('type' => 'flags' , 'data' => array_values( array_map("unserialize", array_unique(array_map("serialize", $data_pause ))) )   ,   'title' => ' ' ,'onSeries' => 'as-1'  , 'shape' => 'square', 'width' => 6 ,'height' => 6,'color' => 'red' , 'fillColor' => 'red') ;//$s = array_column(input, column_key)$json = json_encode( $dataArr['series'] ,true );echo $json;//SELECT `id`, `vps`, `play`, `varied`, `rev`, `max`, `pause`, `lot`, `removed`, `notes`, `cumulative_pips`, `total_pips`, `date_created` FROM `sample_data` WHERE 1//mysqli_close($con);/*'id' => 'as-1' ,       'type' = "flags",      'data' = array( array( x: 1 ) ),      'onSeries' => 'as-1',    }*/